<?php

namespace Factories;

use Contracts\Factories\FactoryContract;
use Faker\Factory;

class ImageButton implements FactoryContract
{
    /**
     * Construct the factory.
     *
     * @param Factory $faker
     */
    public function __construct(Factory $faker)
    {
        $this->faker = $faker->create();
    }

    /**
     * {@inheritdoc}
     */
    public function create($limit = 1, $flatten = false)
    {
        $options = ["", "Bg gradient green", "Bg image dark", "Bg image light", "Icon w/ gradient bg", "Icon w/ image bg", "SVG overlay"];
        for ($i = 1; $i <= $limit; $i++) {
            shuffle($options);
            $data[$i] = [
                'promo_group_id' => '',
                'link' => $this->faker->url,
                'relative_url' => 'http://placekitten.com/360/13'.$i,
                //'relative_url' => '/styleguide/image/360x131?text=360x131%20('.$i.')',
                //'secondary_image' => '/styleguide/image/360x131?text=360x131%20('.$i.')',
                'title' => ucfirst(implode(' ', $this->faker->words(2))),
                'excerpt' => $this->faker->boolean === true ? ucfirst(implode(' ', $this->faker->words(4))) : '',
                'option' => current($options),
            ];

            if ($data[$i]['option'] === 'Icon w/ image bg' || $data[$i]['option'] === 'Icon w/ gradient bg') {
                $data[$i]['secondary_image'] = "data:image/svg+xml;base64,";
            }

            if ($data[$i]['option'] === 'SVG overlay') {
                $data[$i]['secondary_image'] = "data:image/svg+xml;base64,";
            }
        }

        if ($limit === 1 && $flatten === true) {
            return current($data);
        }

        return $data;
    }
}
